mapper.select:
select
  `id`,
  `user_id` as userId,
  `code`,
  `type`,
  `send_time` as sendTime,
  `phone`,
  `mac`,
  `state`,
  `update_time` as updateTime
from user_validation_code


		and id = #{id}


		and code = #{code}


		and type = #{type}


		and send_time = #{sendTime}


		and phone = #{phone}

select `id`, `user_id` as userId, `code`, `type`, `send_time` as sendTime, `phone`, `mac`, `state`, `update_time` as updateTime from user_validation_code
1=1
AND phone = #{phone}
AND type = #{type}
AND user_id = #{userId}
order by send_time desc limit 1;

SELECT ID,USER_ID,CODE,TYPE,SEND_TIME,PHONE,MAC,STATE,UPDATE_TIME
FROM user_validation_code


		AND ID = #{id}


		AND USER_ID = #{userId}


		AND CODE = #{code}


		AND TYPE = #{type}


		AND SEND_TIME = #{sendTime}


		AND PHONE = #{phone}


		AND MAC = #{mac}


		AND SEND_TIME >= (SELECT DATE_SUB(NOW(),INTERVAL #{timeNumber} HOUR)) ORDER BY SEND_TIME DESC
