mapper.update:
update user_device_info set last_login_time = now(),
update_time = #{updateTime},
register_id=null, equipment_id=null where register_id = #{registerId} and equipment_id = #{equipmentId}

mapper.select:
SELECT `id`, `user_id` as userId, `name`, `last_login_time` as lastLoginTime, `register_id` as registerId, `equipment_id` as equipmentId, `time`, `osversion`, `create_time` as createTime, `update_time` as updateTime, `is_deleted` as isDeleted FROM `user_device_info`
is_deleted = 0
AND user_id=#{userId}
AND register_id=#{registerId}
AND equipment_id=#{equipmentId}
LIMIT 1;
