SELECT ID,USER_ID,INVOKE_METHOD,METHOD_VER,RESULT_TYPE,RESULT_CODE,RESULT_MSG,EXCEPTION,DEVICE_MANUFACTURER,DEVICE_MODEL,SYSTEM_VER,APP_VER,VISIT_DATE,IP,INVOKE_PARAM,VISIT_TIME_COST
FROM API_ACCESS_LOG  


		AND ID = #{id}


		AND USER_ID = #{userId}


		AND INVOKE_METHOD = #{invokeMethod}


		AND METHOD_VER = #{methodVer}


		AND RESULT_TYPE = #{resultType}


		AND RESULT_CODE = #{resultCode}


		AND DEVICE_MANUFACTURER = #{deviceManufacturer}


		AND DEVICE_MODEL = #{deviceModel}


		AND SYSTEM_VER = #{systemVer}


		AND APP_VER = #{appVer}


		AND VISIT_DATE = #{visitDate}


		AND IP = #{ip}


		AND INVOKE_PARAM = #{invokeParam}


		AND VISIT_TIME_COST = #{visitTimeCost}

SELECT ID,USER_ID,INVOKE_METHOD,METHOD_VER,RESULT_TYPE,RESULT_CODE,RESULT_MSG,EXCEPTION,DEVICE_MANUFACTURER,DEVICE_MODEL,SYSTEM_VER,APP_VER,VISIT_DATE,IP,INVOKE_PARAM,VISIT_TIME_COST
FROM API_ACCESS_LOG  


		AND ID = #{id}


		AND USER_ID = #{userId}


		AND INVOKE_METHOD = #{invokeMethod}


		AND METHOD_VER = #{methodVer}


		AND RESULT_TYPE = #{resultType}


		AND RESULT_CODE = #{resultCode}


		AND DEVICE_MANUFACTURER = #{deviceManufacturer}


		AND DEVICE_MODEL = #{deviceModel}


		AND SYSTEM_VER = #{systemVer}


		AND APP_VER = #{appVer}


		AND VISIT_DATE  >= date_format(#{fromDate},'%Y-%c-%d %H:%i:%S') 


		AND VISIT_DATE   < date_format(#{toDate},'%Y-%c-%d %H:%i:%S') 


		AND IP = #{ip}


		AND INVOKE_PARAM = #{invokeParam}


		AND VISIT_TIME_COST = #{visitTimeCost}
